import React, { useState } from "react";
import './TodoApp.css';

function TodoApp()
{
    const [value, setvalue]=useState("");

    function addvalue(event)
    {
      var v =event.target.value;
    }
    return(
    <>
      <div className="container">
          <div className="row justify-content-around">
              <div className="col-md-3">
                  <div className="main-div mt-4  rounded shadow-lg p-3 mb-5 bg-white rounded ">
                  <div className="text-center mb-5 bg-info p-1  shadow-lg  mb-5 ">
                  <h4>Todo List</h4>
                  </div>
                  <div className="text-center input-div">
                   <input type="text" name="" id="" placeholder="Add a Item " className=" " onChange={addvalue}/>
                   <button type="button" class="btn btn-info rounded-circle border-top  ">+</button>
                  </div>
                  <ul>
                      <li>{}</li>
                  </ul>
                  </div>
              </div>
          </div>
      </div>
    </>
    );
   
}
export default TodoApp;
